# Simular dados atuais
if "data" not in st.session_state:
    st.session_state["data"] = []

# Dashboard
if st.session_state["data"]:
    df_dash = pd.DataFrame(st.session_state["data"])
    total = len(df_dash)
    andamento = len(df_dash[df_dash["Status"] == "Em andamento"])
    concluido = len(df_dash[df_dash["Status"] == "Concluído"])
    pausado = len(df_dash[df_dash["Status"] == "Pausado"])
    pct_andamento = round((andamento / total) * 100, 1)
    pct_concluido = round((concluido / total) * 100, 1)

    col1, col2, col3 = st.columns(3)
    col1.metric("Projetos Totais", total)
    col2.metric("Em Andamento (%)", f"{pct_andamento}%")
    col3.metric("Concluídos (%)", f"{pct_concluido}%")

    st.subheader("Distribuição por Status")
    fig, ax = plt.subplots()
    ax.pie(df_dash["Status"].value_counts(), labels=df_dash["Status"].value_counts().index,
           autopct='%1.1f%%', startangle=90)
    ax.axis('equal')
    st.pyplot(fig)

# Formulário
st.subheader("Adicionar Novo Projeto")
with st.form("form"):
    col1, col2 = st.columns(2)
    produto = col1.selectbox("Descrição do Produto", options=[""] + product_options)
    tipo_licenca = col2.text_input("Tipo de Licença", value=product_license_map.get(produto, "") if produto else "")
    status = col1.selectbox("Status", ["Em andamento", "Concluído", "Pausado"])
    vendedor = col2.text_input("Nome do Vendedor")
    macrolob = col1.text_input("Macrolob")
    microlob = col2.text_input("Microlob")
    estado = col1.text_input("Estado")
    tipo_venda = col2.selectbox("Tipo de Venda", ["New", "Renew", "Upgrade"])
    data_inicio = col1.date_input("Data de Início")
    obs = st.text_area("Observações")

    submitted = st.form_submit_button("Salvar")
    if submitted:
        st.session_state["data"].append({
            "Produto": produto,
            "Tipo de Licença": tipo_licenca,
            "Status": status,
            "Vendedor": vendedor,
            "Macrolob": macrolob,
            "Microlob": microlob,
            "Estado": estado,
            "Tipo de Venda": tipo_venda,
            "Data de Início": str(data_inicio),
            "Observações": obs
        })
        st.success("Projeto salvo com sucesso!")

# Tabela
if st.session_state["data"]:
    st.subheader("Backlog")
    df_result = pd.DataFrame(st.session_state["data"])
    busca = st.text_input("Buscar por Produto")
    if busca:
        df_result = df_result[df_result["Produto"].str.contains(busca, case=False)]
    st.dataframe(df_result)
    st.download_button("Exportar CSV", df_result.to_csv(index=False), "backlog.csv")
